input.content:
// Chatlog viewer for ClankerOS development sessions

const CHATLOG_FILES = [
    { id: 'ec301782-2723-4689-97f5-46da06e03121', name: 'Session 1 - Foundation and First Boot' },
    // Add more sessions as they become available
];

async function loadChatlog(sessionId) {
    const container = document.getElementById('chatlog-container');
    container.innerHTML = '<p>Loading chatlog...</p>';

    try {
        const response = await fetch(`chatlogs/${sessionId}.jsonl`);
        if (!response.ok) {
            throw new Error(`Failed to load chatlog: ${response.status}`);
        }

        const text = await response.text();
        const lines = text.trim().split('\n').filter(line => line.length > 0);
        const messages = lines.map(line => JSON.parse(line));

        renderChatlog(messages);
    } catch (error) {
        container.innerHTML = `<p style="color: red;">Error loading chatlog: ${error.message}</p>`;
    }
}

function renderChatlog(messages) {
    const container = document.getElementById('chatlog-container');
    container.innerHTML = '';

    for (const msg of messages) {
        // Skip queue operations and other non-message types
        if (msg.type === 'queue-operation' || msg.type === 'summary') {
            continue;
        }

        const messageEl = createMessageElement(msg);
        if (messageEl) {
            container.appendChild(messageEl);
        }
    }

    if (container.children.length === 0) {
        container.innerHTML = '<p>No messages to display.</p>';
    }
}

function createMessageElement(msg) {
    if (!msg.message) {
        return null;
    }

    const div = document.createElement('div');
    const role = msg.message.role || msg.type || 'unknown';
    div.className = `message ${role}`;

    // Header with role and timestamp
    const header = document.createElement('div');
    header.className = 'message-header';
    header.textContent = role.charAt(0).toUpperCase() + role.slice(1);

    if (msg.timestamp) {
        const timestamp = document.createElement('span');
        timestamp.className = 'message-timestamp';
        timestamp.textContent = new Date(msg.timestamp).toLocaleString();
        header.appendChild(timestamp);
    }

    div.appendChild(header);

    // Content
    const content = document.createElement('div');
    content.className = 'message-content';

    if (msg.message.content) {
        if (Array.isArray(msg.message.content)) {
            // Handle content array (new API format)
            for (const item of msg.message.content) {
                if (item.type === 'text') {
                    const textNode = document.createElement('div');
                    textNode.textContent = item.text;
                    content.appendChild(textNode);
                } else if (item.type === 'tool_use') {
                    const toolNode = document.createElement('div');
                    toolNode.className = 'tool-use';
                    toolNode.innerHTML = `<strong>Tool:</strong> ${item.name}<br><pre>${JSON.stringify(item.input, null, 2)}</pre>`;
                    content.appendChild(toolNode);
                }
            }
        } else if (typeof msg.message.content === 'string') {
            // Handle simple string content
            content.textContent = msg.message.content;
        }
    } else {
        content.textContent = '[No content]';
    }

    div.appendChild(content);

    // Show error if present
    if (msg.error) {
        const errorEl = document.createElement('div');
        errorEl.className = 'message-error';
        errorEl.style.color = 'red';
        errorEl.textContent = `Error: ${msg.error}`;
        div.appendChild(errorEl);
    }

    return div;
}

// Initialize selector
function initSelector() {
    const select = document.getElementById('session-select');

    for (const session of CHATLOG_FILES) {
        const option = document.createElement('option');
        option.value = session.id;
        option.textContent = session.name;
        select.appendChild(option);
    }

    select.addEventListener('change', (e) => {
        const sessionId = e.target.value;
        if (sessionId) {
            loadChatlog(sessionId);
        }
    });
}

// Initialize on page load
document.addEventListener('DOMContentLoaded', initSelector);

