def extract_content_parts(content):
    """Extract text and tool calls from message content."""
    text_parts = []
    tool_calls = []

    if isinstance(content, str):
        return content, []
    elif isinstance(content, list):
        for item in content:
            if isinstance(item, dict):
                if item.get('type') == 'text':
                    text_parts.append(item.get('text', ''))
                elif item.get('type') == 'tool_use':
                    tool_calls.append({
                        'name': item.get('name', 'unknown'),
                        'input': item.get('input', {}),
                        'id': item.get('id', '')
                    })
            elif isinstance(item, str):
                text_parts.append(item)
        return '\n'.join(text_parts), tool_calls
    return '', []


def format_timestamp(timestamp_str):
    """Format ISO timestamp to human-readable format."""
    try:
        dt = datetime.fromisoformat(timestamp_str.replace('Z', '+00:00'))
        return dt.strftime('%Y-%m-%d %H:%M:%S')

def parse_jsonl(file_path):
    """Parse JSONL file and extract messages."""
    messages = []

    with open(file_path, 'r') as f:
        for line in f:
            line = line.strip()
            if not line:
                continue

            try:
                entry = json.loads(line)

                # Skip queue operations and summaries for now
                if entry.get('type') in ['queue-operation']:
                    continue

                # Handle summary entries
                if entry.get('type') == 'summary':
                    messages.append({
                        'role': 'summary',
                        'content': entry.get('summary', ''),
                        'timestamp': None
                    })
                    continue

                # Handle regular messages
                if 'message' in entry:
                    msg = entry['message']
                    role = msg.get('role', entry.get('type', 'unknown'))
                    content, tool_calls = extract_content_parts(msg.get('content', ''))
                    timestamp = entry.get('timestamp')

                    messages.append({
                        'role': role,
                        'content': content,
                        'tool_calls': tool_calls,
                        'timestamp': timestamp,
                        'error': entry.get('error'),
                        'uuid': entry.get('uuid')
                    })

            except json.JSONDecodeError as e:
                print(f"Warning: Failed to parse line in {file_path}: {e}")
                continue

    return messages


