file.content:
body {
    font-family: 'Inter', system-ui, -apple-system, sans-serif;
    line-height: 1.6;
    color: #000;
    background-color: #fff;
    margin: 0;
    padding: 2rem;
}

.container {
    max-width: 800px;
    margin: 0 auto;
}

h1 {
    font-size: 1.5rem;
    font-weight: 600;
    margin-bottom: 1.5rem;
}

h2 {
    font-size: 1.25rem;
    font-weight: 600;
    margin-top: 2rem;
    margin-bottom: 1rem;
}

h3 {
    font-size: 1.1rem;
    font-weight: 600;
    margin-top: 1.5rem;
    margin-bottom: 0.5rem;
}

/* Navigation lists - no bullets */
body > .container > ul {
    list-style: none;
    padding: 0;
    margin: 0;
}

/* Content lists - show bullets/numbers */
ul {
    list-style: disc;
    padding-left: 2rem;
    margin: 1rem 0;
}

ol {
    list-style: decimal;
    padding-left: 2rem;
    margin: 1rem 0;
}

li {
    margin-bottom: 0.5rem;
}

a {
    color: #0066cc;
    text-decoration: none;
}

a:hover {
    text-decoration: underline;
}

h1 a {
    color: #000;
}

code {
    font-family: 'JetBrains Mono', 'Consolas', monospace;
    background-color: #f5f5f5;
    padding: 0.2rem 0.4rem;
}

pre {
    background-color: #f5f5f5;
    padding: 1rem;
    overflow-x: auto;
    margin: 1rem 0;
}

pre code {
    background-color: transparent;
    padding: 0;
}

/* Session items */
.session-item {
    margin-bottom: 1rem;
}

.meta {
    color: #666;
    font-size: 0.9rem;
}

/* Chatlog messages */
.chatlog-container {
    margin-top: 1.5rem;
}

.message {
    margin-bottom: 2rem;
    padding-left: 1rem;
    border-left: 2px solid #ddd;
}

.message.user {
    border-left-color: #000;
}

.message-header {
    font-weight: 600;
    margin-bottom: 0.5rem;
}

.message-timestamp {
    font-size: 0.85rem;
    color: #666;
    margin-left: 1rem;
}

.message-content {
    white-space: pre-wrap;
    word-wrap: break-word;
}

.message-error {
    color: #666;
    font-size: 0.9rem;
    margin-top: 0.5rem;
}

/* Tool calls - expandable */
.tool-call {
    margin-top: 1rem;
    border: 1px solid #ddd;
    border-radius: 4px;
}

.tool-call summary {
    padding: 0.5rem;
    cursor: pointer;
    font-weight: 600;
    background-color: #f9f9f9;
    user-select: none;
}

.tool-call summary:hover {
    background-color: #f0f0f0;
}

.tool-call pre {
    margin: 0;
    border-radius: 0 0 4px 4px;
}

.tool-call code {
    font-size: 0.85rem;
}

/* Compact tool calls (for common tools with content) */
.tool-call-compact summary {
    background-color: #f9f9f9;
    border-left: 3px solid #0066cc;
    border-radius: 3px;
}

.tool-call-compact .tool-icon {
    margin-right: 0.5rem;
}

.tool-call-compact .tool-desc {
    color: #666;
    font-size: 0.85rem;
    margin-left: 0.5rem;
}

/* Edit diff display */
.edit-diff {
    padding: 0.5rem;
}

.diff-old, .diff-new {
    margin-bottom: 0.5rem;
}

.diff-old strong {
    color: #d32f2f;
}

.diff-new strong {
    color: #388e3c;
}

/* Tool calls - inline (for common tools) */
.tool-call-inline {
    margin-top: 0.75rem;
    padding: 0.5rem;
    background-color: #f9f9f9;
    border-left: 3px solid #0066cc;
    border-radius: 3px;
    font-size: 0.9rem;
}

.tool-icon {
    margin-right: 0.5rem;
    font-weight: 600;
}

.tool-desc {
    color: #666;
    font-size: 0.85rem;
    margin-left: 0.5rem;
}

